//show hide 함수
udc_obj.detailGrpToggle = function(v) {
	var detailBoxId = options.detailGrpId;
	var compObj = com.util.getComponent(detailBoxId);
	if(!com.util.isEmpty(compObj)){
		if(compObj.getStyleValue("display") == "block") {
			compObj.hide();
			this.removeClass('active');
		} else {
		 	compObj.show();
	        this.addClass('active');
		}
	}
};
