scwin.df_maketAgree = function(_value) {
  if(_value == 'yes'){
    return '<div class="bool_yes">yes</div>';
  } else {
    return '<div class="bool_no">no</div>';
  }
};

scwin.btn_overlayer_onclick = function() {
	$p.$('#grp_overlayerFix').toggleClass('on');

	if($p.$('#grp_overlayerFix').is('.on')){
	    grp_overlayer.setStyle('height','622px');
	} else {		    
	    grp_overlayer.setStyle('height','');
	}

};
