head.script:
	scwin.onpageload = function() {

		 var jsonData = [
		    {
		        "nameDis": "테스트1",
		        "levelCnt": "1"
		    },
		    {
		        "nameDis": "중분류1",
		        "levelCnt": "2"
		    },
		    {
		        "nameDis": "소분류1",
		        "levelCnt": "3"
		    },
		    {
		        "nameDis": "중분류2",
		        "levelCnt": "2"
		    },
		    {
		        "nameDis": "소분류1",
		        "levelCnt": "3"
		    },
		    {
		        "nameDis": "중분류3",
		        "levelCnt": "2"
		    },
		    {
		        "nameDis": "소분류1",
		        "levelCnt": "3"
		    }
		];

		dataList_drill.setJSON( jsonData );


		scwin.pageSet(sbx_sroll, 15 ,dlt_grdData1);
		scwin.pageSet(sbx_checkSelect, 15,dlt_grdData2,pgl_checkSelect);
		scwin.pageSet(sbx_radioSelect, 15,dlt_grdData3,pgl_radioSelect);
		scwin.pageSet(sbx_rowSelect, 15,dlt_grdData4,pgl_rowSelect);
		scwin.pageSet(sbx_fixedColumn, 15,dlt_grdData5);

	//	tbx_total.setValue(dlt_grdAllData.getRowCount());

		for(var i=0; i < 5; i++){
            gen_tbody.insertChild();
        }
	};

	scwin.onpageunload = function() {

	};


	scwin.sbx_select11_onviewchange = function(info) {
		scwin.pageSet(this,info.newValue);
	};


	scwin.pageSet = function(sbxObj , value,dltObj,pageObj) {
		sbxObj.setValue(value);
		var totalRow = dlt_grdAllData.getRowCount();
		var pageCnt = Math.ceil(totalRow /value);
		if(value=="0"){
			dltObj.setJSON(dlt_grdAllData.getAllJSON());
		}else{
			dltObj.setJSON(dlt_grdAllData.getRangeJSON(0,value-1));
		}

		if(pageObj){
			pageObj.setCount(pageCnt);

		}

	};




	scwin.sbx_select_onviewchange = function(info) {
			var sbxArr =[sbx_sroll.id,sbx_checkSelect.id,sbx_radioSelect.id,sbx_rowSelect.id,sbx_fixedColumn.id];
			var dltArr =[dlt_grdData1.id,dlt_grdData2.id,dlt_grdData3.id,dlt_grdData4.id,dlt_grdData5.id];
			var pageArr = ["",pgl_checkSelect.id, pgl_radioSelect.id, pgl_rowSelect.id,""];
		if(sbxArr.indexOf(this.id)>-1){
			dtlId = dltArr[sbxArr.indexOf(this.id)];
			pageId = pageArr[sbxArr.indexOf(this.id)];
			var dltObj = WebSquare.util.getComponentById(dtlId);
			var pageObj = WebSquare.util.getComponentById(pageId);
			scwin.pageSet(this, info.newValue ,dltObj,pageObj);
		}
	};


	scwin.pageList_onviewchange = function(info) {

		var sbxArr =[sbx_sroll.id,sbx_checkSelect.id,sbx_radioSelect.id,sbx_rowSelect.id,sbx_fixedColumn.id];
		var dltArr =[dlt_grdData1.id,dlt_grdData2.id,dlt_grdData3.id,dlt_grdData4.id,dlt_grdData5.id];
		var pageArr = ["",pgl_checkSelect.id, pgl_radioSelect.id, pgl_rowSelect.id,""];

		if(pageArr.indexOf(this.id)>-1){
			dtlId = dltArr[pageArr.indexOf(this.id)];
			sbxId = sbxArr[pageArr.indexOf(this.id)];
			var dltObj = WebSquare.util.getComponentById(dtlId);
			var sbxIdObj = WebSquare.util.getComponentById(sbxId);

			var newIdx = info.newSelectedIndex;
			var stIdx = sbxIdObj.getValue() * (newIdx-1)
			var edIdx = (sbxIdObj.getValue())*newIdx-1;

			dltObj.setJSON(dlt_grdAllData.getRangeJSON(stIdx,edIdx));
		}

	};
